import React, { Component } from "react";
import "./task.css";

export default class Task extends Component {
  state = {
    description: "",
  };
  onDescriptionChange = (e) => {
    this.setState({
      description: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    this.props.onItemAdded(this.state.description);
  };

  render() {
    const {
      description,
      created,
      onDeleted,
      onToggleCompleted,
      completed,
      onToggleEdit,
      edit,
    } = this.props;

    let className;
    let checked = "";
    if (completed) {
      className = "completed";
      checked = "checked";
    }
    if (edit) className = "editing";

    return (
      <li className={className}>
        <div className="view">
          <input
            className="toggle"
            type="checkbox"
            onChange={onToggleCompleted}
            checked={checked}
          />
          <label>
            <span className="description" on onClick={onToggleCompleted}>
              {description}
            </span>
            <span className="created">{created}</span>
          </label>
          <button className="icon icon-edit" onClick={onToggleEdit}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
        <form >
          <input
            className="edit"
            defaultValue={description}
            onChange={this.onDescriptionChange}
          />
        </form>
      </li>
    );
  }
}
