import React, { Component } from 'react';
import './task.css';

export default class Task extends Component {
  // state = {
  //   description: '',
  // };

  // onDescriptionChange = (evt) => {
  //   this.setState({
  //     description: evt.target.value,
  //   });
  // };

  // onSubmit = (evt) => {
  //   evt.preventDefault();
  //   const { onItemAdded } = this.props;
  //   const { description } = this.state;
  //   onItemAdded(description);
  // };

  render() {
    const { description, created, onDeleted, onToggleCompleted, completed, onToggleEdit, edit } = this.props;

    let className;
    let checked = '';
    if (completed) {
      className = 'completed';
      checked = 'checked';
    }
    if (edit) className = 'editing';

    return (
      <li className={className}>
        <div className="view">
          <input className="toggle" type="checkbox" onChange={onToggleCompleted} checked={checked} />
          <label>
            <span className="description">
              {description}
            </span>
            <span className="created">{created}</span>
          </label>
          <button type = 'submit' className="icon icon-edit" onClick={onToggleEdit}>t</button>
          <button type = 'submit' className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
        <form>
          <input className="edit" defaultValue={description} onChange={this.onDescriptionChange} />
        </form>
      </li>
    );
  }
}
